import React, { useState, useEffect } from "react";

function CertainRecipe() {

const [recipetitle, setRecipeTitle] = useState("");
const [userRecipe, setUserRecipe] = useState([]);
const [steps, setSteps] = useState([]);

  const getQueryParam = (param) => {
    const params = new URLSearchParams(window.location.search);
    return params.get(param);
  };

  const getRecipe = async () => {
    const title = getQueryParam('title');
    if (!title) {
      return;
    }

    const user = {
        _id: title,
      };

      const responsetwo = await fetch(' http://localhost:8001/getCertainRecipe', {
        method: 'POST', 
        headers: { 'Content-Type': 'application/json' }, 
        credentials: 'include', 
        body: JSON.stringify(user) 
      })

      if (responsetwo.ok) {
        const data2 = await responsetwo.json();
        setUserRecipe(data2);
      }

      const responsethree = await fetch(' http://localhost:8001/getCertainRecipe', {
        method: 'POST', 
        headers: { 'Content-Type': 'application/json' }, 
        credentials: 'include', 
        body: JSON.stringify(user) 
      })

      if (responsetwo.ok) {
        const data2 = await responsetwo.json();
        setUserRecipe(data2);
      }




    setRecipeTitle(title);
  };

  useEffect(() => {
    getRecipe();
   }, []);


return(
<>
<h>{recipetitle}</h>

<div>
{userRecipe.map((listing) => (
  <div key={listing.id} >
    <h2>{listing.title}</h2>
    <p>{listing.description}</p>
    <p>
      <b>Date Posted:</b> {listing.date.slice(0, 10)}
    </p>


  </div>
))}
</div>

</>
);


}

export default CertainRecipe;
