import React, { useState } from 'react';


function HomePage() {
  const [isHovering, setIsHovering] = useState(false);

  const handleClick = (action) => {
    alert(`${action} button clicked!`);
  };

  const handleMouseEnter = () => {
    setIsHovering(true);
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
  };

  return (
    <div style={styles.container}>
      {/* Left Section */}
      <div style={styles.leftSection}>
        <h2 style={styles.subtitle}>A new way to cook</h2>
        <h1 style={styles.title}>The Recipe Page</h1>
        <p style={styles.description}>Carry your recipe book everywhere</p>

        {/* Buttons */}
        <div style={styles.buttonContainer}>
          <button onClick={() => handleClick("Sign In")} onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave} style={isHovering ?styles.hoverbutton : styles.button }>
            Sign In
          </button>
          <button style={styles.button} onClick={() => handleClick("Sign Up")} onMouseEnter={handleMouseEnter}
       onMouseLeave={handleMouseLeave}>
            Sign Up
          </button>
        </div>
      </div>

      {/* Right Section */}
      <div style={styles.rightSection}>
        <img
          src="https://static.toiimg.com/photo/64029527.cms?imgsize=797893" // Replace with your image URL
          alt="Delicious food"
          style={styles.image}
        />
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    width: "100%",
    height: "100vh",
    fontFamily: "'Georgia', serif",
    color: "#ffffff",
  },

  leftSection: {
    flex: 1,
    backgroundColor: "#97AFCB", // Matches the light blue shade
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center",
    padding: "2rem",
  },
  subtitle: {
    fontSize: "1.2rem",
    marginBottom: "0.5rem",
  },
  title: {
    fontSize: "3rem",
    fontWeight: "bold",
    margin: "0.5rem 0",
  },
  description: {
    fontSize: "1rem",
    marginBottom: "1.5rem",
  },
  buttonContainer: {
    display: "flex",
    flexDirection: "row",
    gap: "1rem",
  },
  button: {
    backgroundColor: "#ffffff",
    color: "#000000",
    border: "none",
    borderRadius: "30px",
    padding: "0.8rem 3rem",
    fontSize: "1rem",
    fontWeight: "bold",
    cursor: "pointer",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",

  },

  buttonHover: {
    opacity: "0.9",
    //color: "#e7e7e7",
  },

  hoverbutton:{
    backgroundColor: "#e7e7e7",
    color: "#000000",
    border: "none",
    borderRadius: "30px",
    padding: "0.8rem 3rem",
    fontSize: "1rem",
    fontWeight: "bold",
    cursor: "pointer",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
  },


  rightSection: {
    flex: 1,
    overflow: "hidden",
  },
  image: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
  },
};

export default HomePage;
