import React, { useState, useEffect, useRef } from "react";


function EditRecipe() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  //const [inputs, setInputs] = useState([{ id: 1, value: "" }]); // Initial input section
  const [inputs, setInputs] = useState([]);
  const [id, setId] = useState(null);
  const [userRecipe, setUserRecipe] = useState([]);
  const[steps, setSteps] = useState([])
  const [headerBools, setHeaderBools] = useState([false, false, false])
  const [photo, setPhoto] = useState('');
  const profilePhotoRef = useRef();


  const handleMouseEnter = (id) => {
      setHeaderBools((prevStates) => {
        const updatedStates = [...prevStates];
        updatedStates[id] = true;
        return updatedStates;
      });
    };

    const handleMouseLeave = (id) => {
      setHeaderBools((prevStates) => {
        const updatedStates = [...prevStates];
        updatedStates[id] = false;
        return updatedStates;
      });
    };


    const getQueryParam = (param) => {
        const params = new URLSearchParams(window.location.search);
        return params.get(param);
      };

const getSteps = async (title) => {

    const user = {
        _id: title,
      };

    const responsetwo = await fetch(' http://localhost:8001/getSteps', {
        method: 'POST', 
        headers: { 'Content-Type': 'application/json' }, 
        credentials: 'include', 
        body: JSON.stringify(user) 
      })

      if (responsetwo.ok) {
        const data2 = await responsetwo.json();
        const fetchedSteps = data2[0].steps;
        setSteps(fetchedSteps);
        setInputs(fetchedSteps.map((step, index) => ({ id: index + 1, value: step })));
      }


};


const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();

      // Read the file as a binary string to convert to Buffer later
      reader.onload = (event) => {
        const buffer = new Uint8Array(event.target.result); // Convert to Buffer
        setPhoto({
          data: buffer, // Store the buffer in the state
          contentType: file.type, // Store the MIME type
        });
      };

      reader.readAsArrayBuffer(file); // Read the file as an ArrayBuffer
    }
  };



      const getRecipe = async () => {
        const title = getQueryParam('title');
        if (!title) {
          return;
        }
      //  alert(title);
        setId(title);
        getSteps(title);

        const user = {
            _id: title,
          };

          const responsetwo = await fetch(' http://localhost:8001/getCertainRecipe', {
            method: 'POST', 
            headers: { 'Content-Type': 'application/json' }, 
            credentials: 'include', 
            body: JSON.stringify(user) 
          })

          if (responsetwo.ok) {
            const data2 = await responsetwo.json();
            setUserRecipe(data2);
            setTitle(data2[0].title);
            setDescription(data2[0].description);

          }

      };

      useEffect(() => {
        getRecipe();
       }, [] );


       const handleAddInputFirst = () => {
        setInputs(prevInputs => {
            // Create a new input with ID of 1
            const newInput = { id: 1, value: "" };

            // Update the rest of the inputs to have incremented IDs
            const updatedInputs = prevInputs.map(input => ({
                ...input,
                id: input.id + 1
            }));

            // Add the new input at the start of the array
            return [newInput, ...updatedInputs];
        });
    };


    const handleAddInput = (id) => {
        setInputs(prevInputs => {
          const updatedInputs = [];
          let newId = id + 1;

          prevInputs.forEach(input => {
            updatedInputs.push(input);
            if (input.id === id) {

              updatedInputs.push({ id: newId, value: "" });
            }
          });

          // Reassign sequential IDs to maintain order
          return updatedInputs.map((input, index) => ({ ...input, id: index + 1 }));
        });
      };




//   const handleAddInput = () => {
//     const newInput = { id: inputs.length + 1, value: "" };
//     setInputs([...inputs, newInput]); // Add the new input to the state
//   };




  const handleInputChange = (id, newValue) => {
    setInputs(inputs.map(input => 
      input.id === id ? { ...input, value: newValue } : input
    ));
  };

  const handleDeleteInput = (id) => {
    const updatedInputs = inputs.filter(input => input.id !== id);
    // Reassign step numbers (IDs) based on the updated array index
    const reassignedInputs = updatedInputs.map((input, index) => ({
      ...input,
      id: index + 1, // Ensure IDs are sequential
    }));
    setInputs(reassignedInputs);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8001/returnusername', {
        method: 'GET',
        credentials: 'include', // Include session cookies
      });

      if (response.ok) {
        const data = await response.json();
        if (data.username) {
          const steps = inputs.map(input => input.value);

        const formData = new FormData();
        formData.append('title', title);
        formData.append('_id', id);
        formData.append('username', data.username);
        formData.append('description', description);

        if (photo) {
            formData.append('photo', new Blob([photo.data], { type: photo.contentType }), 'photo.jpg');
          }

          inputs.forEach((input, index) => {
            formData.append(`steps[${index}]`, input.value);
          });




          const response = await fetch('http://localhost:8001/editrecipe', {
            method: 'POST',
            // headers: { 'Content-Type': 'application/json' },
            // credentials: 'include',
           // body: JSON.stringify(recipe) // Converts the recipe object into a JSON string
           body: formData,
          });




          if (response.ok) {
            alert(title + " Recipe edited successfully!");
            window.location.href=  `/certainrecipe?title=${encodeURIComponent(id)}`


          } else {
            alert("Failed to create recipe.");
          }
        }
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      alert('An error occurred. Please try again.');
    }
  };

  return (
    <>
 <div >   

<header style = {styles.headerdiv}>
<a href = "/yourrecipe" 
onMouseEnter={() => handleMouseEnter(0)}
onMouseLeave={() => handleMouseLeave(0)}
style={headerBools[0] ? styles.linkHover : styles.link}> 
Your Recipes 
</a>

<a href = "/signin" 
onMouseEnter={() => handleMouseEnter(1)}
onMouseLeave={() => handleMouseLeave(1)}
style={headerBools[1] ? styles.linkHover : styles.link}> 
Featured Recipes </a>

<a href = "/signin" 
onMouseEnter={() => handleMouseEnter(2)}
onMouseLeave={() => handleMouseLeave(2)}
style={headerBools[2] ? styles.linkHover : styles.link}>
 Meal Generator </a>

</header>

</div>
<div style={styles.fullpage}>
    <form onSubmit={handleSubmit} style={styles.form}>
        <h1 style={styles.header}> Edit Your Recipe</h1>
      <label htmlFor="title" style={styles.label}>Title:</label>
      <input
        type="text"
        id="title"
        value={title}
        style={styles.input}
        onChange={(e) => setTitle(e.target.value)}
      />

      <label htmlFor="description" style={styles.label}>Description:</label>
      <input
        type="text"
        id="description"
        value={description}
        style={styles.input}
        onChange={(e) => setDescription(e.target.value)}
      />

<label htmlFor="photo" style={styles.Photolabel}>Photo (Optional):</label>
<input
        type="file"
        name="photo"
        accept="image/*"
        //required
        ref={profilePhotoRef}
        onChange={handleFileChange}
        style = {styles.photoInput}
      />



<button type="button" onClick={() => handleAddInputFirst(0) }  style={styles.Deletebutton}>
        Add Another Step Below
      </button>


      {inputs.map(input => (
        <div key={input.id}>
          <label htmlFor={`input-${input.id}`} style={styles.label}>
            Step {input.id}:
          </label>
          <input
            id={`input-${input.id}`}
            type="text"
            style={styles.input}
            value={input.value}
            onChange={(e) => handleInputChange(input.id, e.target.value)}
            required
          />
          <button
            type="button"
            style={styles.Deletebutton}
            onClick={() => handleDeleteInput(input.id) } // Add a delete button
          >
            Delete Step
          </button>

          <button type="button" onClick={() => handleAddInput(input.id) } style={styles.Deletebutton}>
        Add Another Step
      </button>

        </div>
      ))}




      <button type="submit" style={styles.button}>Edit Recipe</button>
    </form>
    </div>

    </>

  );
