import React, { useState, useEffect } from "react";


function GenerateSevenDay() {
const [headerBools, setHeaderBools] = useState([false, false, false]);
const [isOn, setIsOn] = useState(false);

    const toggleSwitch = () => {
      setIsOn((prevState) => !prevState);
    };

    const handleMouseEnter = (id) => {
        setHeaderBools((prevStates) => {
          const updatedStates = [...prevStates];
          updatedStates[id] = true;
          return updatedStates;
        });
      };

      const handleMouseLeave = (id) => {
        setHeaderBools((prevStates) => {
          const updatedStates = [...prevStates];
          updatedStates[id] = false;
          return updatedStates;
        });
      };




return(
<>

<header style = {styles.headerdiv}>
<a href = "/yourrecipe" 
onMouseEnter={() => handleMouseEnter(0)}
onMouseLeave={() => handleMouseLeave(0)}
style={headerBools[0] ? styles.linkHover : styles.link}
> 
Your Recipes 
</a>

<a href = "/signin" 
onMouseEnter={() => handleMouseEnter(1)}
onMouseLeave={() => handleMouseLeave(1)}
style={headerBools[1] ? styles.linkHover : styles.link}
> 
Featured Recipes </a>

<a href = "/mealgenerator" 
onMouseEnter={() => handleMouseEnter(2)}
onMouseLeave={() => handleMouseLeave(2)}
style={headerBools[2] ? styles.linkHover : styles.link}
>
 Meal Generator </a>

</header>

<h style = {styles.header}>Generate a Meal Plan for Seven Days </h>

<div style={styles.switchContainer}>
        <div style={styles.switch} onClick={toggleSwitch}>
          <div style={styles.switchCircle}></div>
        </div>
      </div>

</>

);

}


const styles = {

    headerdiv:{
        display: "flex",
        justifyContent: "space-around",
        backgroundColor: "#577493",
        padding: "1rem",
        fontFamily: "'Georgia', serif",
        marginBottom: "3rem",
      },

      link: {
        color: "#ffffff",
        textDecoration: "none",
        fontSize: "1.2rem",
        fontWeight: "bold",
    },


    linkHover: {
        color: "#000000",
        textDecoration: "none",
        fontSize: "1.2rem",
        fontWeight: "bold",
    },

    header: {
        fontSize: "3rem",
        fontFamily: "'Georgia', serif",
        textAlign: "center",
        color: "#000000",
       // marginBottom: "2rem",
        marginTop: "2rem",
        marginBottom: "2.5rem",
        position: "relative", // Ensure proper positioning
        top: 0,
        zIndex: 10,
        padding: "1rem",
        width: "100%",
        display: "flex", // Enables Flexbox
        alignItems: "center", // Centers content vertically
        justifyContent: "center", // Centers content horizontally
        fontWeight: "bold"
      },


      switchContainer: {
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        marginTop: "1rem",
      },

      switch: {
        width: "60px",
        height: "30px",
        backgroundColor: "green" ,
        borderRadius: "15px",
        display: "flex",
        alignItems: "center",
        padding: "5px",
        cursor: "pointer",
        transition: "background-color 0.3s ease-in-out",
      },

      IsOnswitch: {
        width: "60px",
        height: "30px",
        backgroundColor: "red",
        borderRadius: "15px",
        display: "flex",
        alignItems: "center",
        padding: "5px",
        cursor: "pointer",
        transition: "background-color 0.3s ease-in-out",
      },

      switchCircle: {
        width: "20px",
        height: "20px",
        backgroundColor: "#ffffff",
        borderRadius: "50%",
        transition: "transform 0.3s ease-in-out",
        transform: isOn ? "translateX(30px)" : "translateX(0)",
      },

      switchCircle: {
        width: "20px",
        height: "20px",
        backgroundColor: "#ffffff",
        borderRadius: "50%",
        transition: "transform 0.3s ease-in-out",
        transform:"translateX(30px)" ,
      },
};


export default GenerateSevenDay
