import React, { useState } from "react";


function YourRecipePage() {

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [username, setUsername] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault(); 
        try {
          const response = await fetch('http://localhost:8001/returnusername', {
            method: 'GET',
            credentials: 'include', // Include session cookies
          });

          if (response.ok) {
            const data = await response.json();
            if (data.username) {
              //alert( data.username);
              setUsername(data.username);

              const recipe = {
                title,
                description,
                username
              };


              const response = await fetch(' http://localhost:8001/addrecipe', {
                method: 'POST', 
                headers: { 'Content-Type': 'application/json' }, 
                credentials: 'include', 
                body: JSON.stringify(user) // converts the user object into a json string so that it can be sent to the request body because it is currently a javascript object.
              })




            } 
          } 

        } catch (error) {
          console.error('Error fetching data:', error);
          alert('An error occurred. Please try again.');
        }
      };

return(
    <form onSubmit={handleSubmit} >
        <label htmlFor="title">
          Title:
        </label>
        <input
          type="text"
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label htmlFor="description">
        Description:
        </label>
        <input
          type="text"
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />


          <button
          type="submit"
          >
          Create New Recipe
        </button>




    </form>

);

};


export default YourRecipePage;



