import React, { useState, useEffect } from "react";

function CertainRecipe() {


const [userRecipe, setUserRecipe] = useState(null);
const [id, setId] = useState(0);
//const [imageData, setImageData] = useState(null);


const [headerBools, setHeaderBools] = useState([false, false, false])



const handleDelete = async () => {

const user = {
    _id: id,
  };

  const responsetwo = await fetch(' http://localhost:8001/deleterecipe', {
    method: 'POST', 
    headers: { 'Content-Type': 'application/json' }, 
    credentials: 'include', 
    body: JSON.stringify(user) 
  })

  if (responsetwo.ok) {
    alert("Recipe Deleted Successfully");
    window.location.href=  `/yourrecipe`

  }





};

const handleEdit = () =>{
 //  window.location.href = "/editrecipe";

 window.location.href=  `/editrecipe?title=${encodeURIComponent(id)}`

    };

const handleMouseEnter = (id) => {
    setHeaderBools((prevStates) => {
      const updatedStates = [...prevStates];
      updatedStates[id] = true;
      return updatedStates;
    });
  };

  const handleMouseLeave = (id) => {
    setHeaderBools((prevStates) => {
      const updatedStates = [...prevStates];
      updatedStates[id] = false;
      return updatedStates;
    });
  };


  const getQueryParam = (param) => {
    const params = new URLSearchParams(window.location.search);
    return params.get(param);
  };

  const getRecipe = async () => {
    const title = getQueryParam('title');
    if (!title) {
      return;
    }
    setId(title);

    const user = {
        _id: title,
      };

      const responsetwo = await fetch(' http://localhost:8001/getCertainRecipe', {
        method: 'POST', 
        headers: { 'Content-Type': 'application/json' }, 
        credentials: 'include', 
        body: JSON.stringify(user) 
      })

      if (responsetwo.ok) {
        const data2 = await responsetwo.json();
        setUserRecipe(data2);

      }

  };

  useEffect(() => {
    getRecipe();
   },  );


return(
<>

    <div style={styles.content}>
    {userRecipe ? (
      <div>
        <h2 style={styles.title}>{userRecipe.title}</h2>
        <p style={styles.description}>{userRecipe.description}</p>
        <p style={styles.date}>
          <b>Date Posted:</b> {new Date(userRecipe.date).toLocaleDateString()}
        </p>

        {userRecipe.photo && userRecipe.photo.data && userRecipe.photo.contentType ? (
          <img
            src={userRecipe.photo.data}
            alt={userRecipe.title}
            style={{ maxWidth: "100%", height: "auto", marginBottom: "1rem" }}
          />
        ) : (
          <p style={{ textAlign: "center", color: "#888" }}>No photo available</p>
        )}

        {userRecipe.steps && userRecipe.steps.length > 0 ? (
          <div style={styles.stepsContainer}>
            <h3 style={styles.stepsTitle}>
              <b>Steps:</b>
            </h3>
            <ul style={styles.stepsList}>
              {userRecipe.steps.map((step, index) => (
                <li key={index} style={styles.step}>
                  <b>Step {index + 1}: </b>
                  {step}
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <p style={styles.noSteps}>No Steps Provided</p>
        )}
      </div>
    ) : (
      <p>Loading recipe...</p>
    )}
  </div>

  </>
);


