import React, { useState, useEffect } from "react";

function CertainRecipe() {


const [userRecipe, setUserRecipe] = useState([]);
const [headerBools, setHeaderBools] = useState([false, false, false])

const handleMouseEnter = (id) => {
    setHeaderBools((prevStates) => {
      const updatedStates = [...prevStates];
      updatedStates[id] = true;
      return updatedStates;
    });
  };

  const handleMouseLeave = (id) => {
    setHeaderBools((prevStates) => {
      const updatedStates = [...prevStates];
      updatedStates[id] = false;
      return updatedStates;
    });
  };


  const getQueryParam = (param) => {
    const params = new URLSearchParams(window.location.search);
    return params.get(param);
  };

  const getRecipe = async () => {
    const title = getQueryParam('title');
    if (!title) {
      return;
    }

    const user = {
        _id: title,
      };

      const responsetwo = await fetch(' http://localhost:8001/getCertainRecipe', {
        method: 'POST', 
        headers: { 'Content-Type': 'application/json' }, 
        credentials: 'include', 
        body: JSON.stringify(user) 
      })

      if (responsetwo.ok) {
        const data2 = await responsetwo.json();
        setUserRecipe(data2);
      }







  };

  useEffect(() => {
    getRecipe();
   }, [] );


return(
<>


<header style = {styles.headerdiv}>
<a 
href = "/yourrecipe" 
onMouseEnter={() => handleMouseEnter(0)}
onMouseLeave={() => handleMouseLeave(0)}
style={headerBools[0] ? styles.headerafterWord : styles.headerWord}> 
Your Recipes 
</a>

<a href = "/signin" 
          onMouseEnter={() => handleMouseEnter(1)}
          onMouseLeave={() => handleMouseLeave(1)}
style={headerBools[1] ? styles.headerafterWord : styles.headerWord}> 
Featured Recipes </a>

<a href = "/signin" 
          onMouseEnter={() => handleMouseEnter(2)}
          onMouseLeave={() => handleMouseLeave(2)}
style={headerBools[2] ? styles.headerafterWord : styles.headerWord}>
 Meal Generator </a>

</header>




      <div >
        {userRecipe.map((listing) => (
          <div key={listing.id } style={styles.fullpage} >
            <h2 style= {styles.header}>{listing.title} </h2>
            <div >
            <p>{listing.description}</p>
            <p>
              <b>Date Posted:</b> {listing.date.slice(0, 10)}
            </p>
            {listing.steps && listing.steps.length > 0 &&  (
            <div style={styles.divform} >
              <ul style={styles.form}>
                {listing.steps.map((step, index) => (
                  <li key={index}>
                    <b>Step {index + 1}: </b>{step}
                  </li>
                )) }
              </ul>
              </div>
            )}      
            </div>
            {listing.steps && listing.steps.length === 0 && (
                        <p style={styles.form}> No Steps Provided</p>
            )}

          </div>
        ))}
      </div>
    </>
);


}


const styles = {
    fullpage: {
        width: "100%",
        minHeight: "100vh", // Ensures the background covers the entire page content
       textAlign: "center",
        padding: "2rem 0", // Adds spacing at the top and bottom
        fontFamily: "'Georgia', serif",
      },

      header: {
        fontSize: "3rem",
        fontFamily: "'Georgia', serif",
        textAlign: "center",
        color: "#000000",
        marginBottom: ".47rem",
       // marginTop: ".5rem",
        position: "relative", // Ensure proper positioning
        top: 0,
        zIndex: 10,
        padding: "1rem",
        width: "100%",
        display: "flex", // Enables Flexbox
        alignItems: "center", // Centers content vertically
        justifyContent: "center", // Centers content horizontally
        fontWeight: "bold"
      },

      linkp:{
        textDecoration: "none",
          color: 'inherit',
      },


      form: {
        display: "flex",
        flexDirection: "column",
        alignItems: "left",
       backgroundColor: "#f4f4f4",
        fontFamily: "'Georgia', serif",
        border: "3px solid #000",
        borderRadius: "15px",
        padding: "2rem 3rem",
        width: "450px",
        boxSizing: "border-box",
        margin: "1rem",
        textAlign: "left",

      },


      divform:{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#ffffff",
        textAlign: "center",

      },

      fullform: {
        width: "100vw", // Use full viewport width
        display: "flex",
        alignItems: "flex-start",
        flexDirection: "row",
        flexWrap: "wrap",
        padding: "1rem 1.5rem",
        boxSizing: "border-box", // Ensure border/padding doesn’t reduce width
      },


      label: {
        fontSize: "1.5rem",
        fontFamily: "'Georgia', serif",
        color: "#000000",
        marginBottom: "1.25rem",
        textAlign: "left",
        width: "100%",
      },


      input: {
        width: "100%",
        padding: "0.8rem",
        fontSize: "1.2rem",
        borderRadius: "15px",
        border: "1px solid #000",
        marginBottom: "1rem",
        boxSizing: "border-box",
      },



     Addbutton: {
        backgroundColor: "#ffffff",
        color: "#000000",
        border: "1.9px solid #000",
        borderRadius: "30px",
        padding: "0.7rem 1.75rem",
        fontSize: "1rem",
        fontWeight: "bold",
        cursor: "pointer",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
        transition: "background-color 0.3s ease",
        margin: "0.6rem"
      },


      button: {
        backgroundColor: "#ffffff",
        color: "#000000",
        border: "2px solid #000",
        borderRadius: "30px",
        padding: "0.8rem 3rem",
        fontSize: "1rem",
        fontWeight: "bold",
        cursor: "pointer",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
        transition: "background-color 0.3s ease",
        margin: "0.6rem"
      },


      headerdiv:{
        backgroundColor: "#577493",
        padding: "1rem 1.5rem",
        border: "1px solid #000",
        borderRadius: "2px",
        marginBottom: "30px",
      },

      linkfor:{
        padding: "3rem 3.5rem",
        color:"#000000",
        fontSize: "1.2rem",
        margin: "0.5rem",
        fontWeight: "bold",
        fontFamily: "'Georgia', serif",
        textDecoration: "none",

      },

      headerWord:{
        padding: "1rem 3.5rem",
        color:"#ffffff",
        fontSize: "1.2rem",
        margin: "0.2rem",
        fontWeight: "bold",
        fontFamily: "'Georgia', serif",
        textDecoration: "none",
      },

      headerafterWord:{
        padding: "1rem 3.5rem",
        color:"#000000",
        fontSize: "1.2rem",
        margin: "0.2rem",
        fontWeight: "bold",
        fontFamily: "'Georgia', serif",
        textDecoration: "none",
      }



};








export default CertainRecipe;
